    var body: some View {
        NavigationView {
            List(vitamins) { vitamin in
                NavigationLink(destination: VitaminDetailView(vitamin: vitamin)) {
                    VStack(alignment: .leading) {
                        Text(vitamin.name)
                            .font(.headline)
                        Text(vitamin.shortDescription)
                            .font(.subheadline)
                            .foregroundColor(.gray)
                    }
                    .padding(.vertical, 5)
                }
            }
            .navigationTitle("Справочник витаминов")
        }
    }
